import React from "react";
import { Component } from "react";

class ExpenseItem extends Component {
  constructor() {
    super();

    this.state = {
      name: "Sabina",
    };
  }

  render() {
    return (
      <div>
        <h1>{this.state.name}</h1>
        <button
          onClick={() => {
            this.setState({ name: "Ovidiu" });
          }}
        >
          Change Name
        </button>
      </div>
    );
  }
}

export default ExpenseItem;
